describe('Copy', function(){

    var copy = require('../copy')

    it('should only copy own props', function () {
        var person = {
            name: 'john',
            age: 1
        }

        var child = Object.create(person)
        child.birth = 2000


        var obj = copy(child)

        Object.keys(obj)
            .should
            .eql(['birth'])

        copy(person)
            .should
            .eql({name: 'john', age: 1})

    })
})