describe('CopyKeys', function(){

    var copyKeys = require('../copyKeys')

    it('should copy keys', function () {
        var person = {
            name: 'john',
            age: 1
        }

        copyKeys(person, {}, Object.keys(person))
            .should
            .eql({name: 'john', age: 1})

        copyKeys(person)
            .should
            .eql({name: 'john', age: 1})

        copyKeys(person, {}, {name: 'firstName'})
            .should
            .eql({
                firstName: 'john'
            })

    })
})